package _02_robot_graffiti;

import org.jointheleague.graphical.robot.Robot;

public class RobotGraffiti {

	public static void main(String[] args) {
		Robot r = new Robot();
		r.penDown();
		r.setSpeed(400);
		r.hide();
		r.turn(-15);
		r.move(100);
		r.turn(180);
		r.move(100);
		r.turn(-130);
		r.move(50);
		r.turn(130);
		r.move(50);
		r.turn(-130);
		r.move(100);
		
		
		// TODO Auto-generated method stub

	}

}              //W
